import styled from 'styled-components'

export const AuthScreen = styled.div`
  height: 472px;
  width: 400px;
  background: #5484f8;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  color: #fff;

  div {
    display: flex;
    flex-direction: column;
    align-items: center;

    h1 {
      margin-bottom: 20px;
      font-weight: 700;
      font-size: 52px;
    }

    p {
      font-weight: 300;
    }
  }
`

export const Container = styled.div`
  display: flex;
  justify-content: center;
`

export const Frame = styled.div`
  height: 400px;
  width: 400px;
  margin: 0;
  padding: 0;
  position: relative;

  > img {
    height: 100%;
    width: 100%;
    height: auto;
    margin: 0;
    padding: 0;
  }

  > div {
    height: 70px;
    background: rgba(255, 255, 255, 0.8);
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    display: flex;
    align-items: center;

    img {
      width: 107px;
      height: 55px;
      margin-left: 20px;
    }

    div {
      margin-left: 20px;

      span {
        font-weight: 300;
        font-size: 22px;
        color: #1f1f1f;
        display: block;
      }

      strong {
        font-weight: 700;
        font-size: 24px;
        color: #646060;
      }
    }
  }
`

export const ButtonContainer = styled.div`
  width: 400px;
  display: flex;
  justify-content: space-between;
`
import styled from 'styled-components'
import { shade } from 'polished'

export const Container = styled.button`
  background: #5484f8;
  height: 56px;
  border: 0;
  padding: 0 16px;
  color: #fff;
  width: 195px;
  font-weight: 300;
  margin-top: 16px;
  transition: background-color 0.2s;

  &:hover {
    background: ${shade(0.2, '#5484f8')};
  }
`
import React from 'react'

import { Container } from './styles'

const Button = ({ children, ...rest }) => (
  <Container type='button' {...rest}>
    {children}
  </Container>
)

export default Button
import React, { Component } from 'react'

import firebase from 'firebase'
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth'

import { Frame, ButtonContainer, Container, AuthScreen } from './styles'
import Logo from '/../../assets/logo.png'

import html2canvas from 'html2canvas'

import Button from '../../components/Button'

firebase.initializeApp({
  apiKey: 'AIzaSyDflrkP2uTFEkOtT_KDRcDPgjjDvFd9MQE',
  authDomain: 'profilemaster-b369d.firebaseapp.com'
})

class SignIn extends Component {
  state = { isSignedIn: false, downloadURL: '' }

  uiConfig = {
    signInFlow: 'popup',
    signInOptions: [firebase.auth.GithubAuthProvider.PROVIDER_ID],
    callbacks: {
      signInSuccess: () => false
    }
  }

  componentDidMount = () => {
    firebase.auth().onAuthStateChanged((user) => {
      this.setState({ isSignedIn: !!user })
    })
  }

  download(dataUrl) {
    const element = document.createElement('a')
    element.setAttribute('href', dataUrl)
    element.setAttribute('download', 'profile-master.png')

    element.style.display = 'none'
    document.body.appendChild(element)

    element.click()

    document.body.removeChild(element)
  }

  handleDownload() {
    html2canvas(document.getElementById('capture'), {
      useCORS: true,
      height: 400,
      width: 400,
      scrollY: -window.scrollY
    }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png')
      this.download(imgData)
    })
  }

  render() {
    return (
      <Container className='App'>
        {this.state.isSignedIn ? (
          <span>
            <Frame id='capture'>
              <img src={firebase.auth().currentUser.photoURL} alt='profile' />
              <div>
                <img src={Logo} alt='Logo GDG Juiz de Fora' />
                <div>
                  <span>
                    {firebase.auth().currentUser.providerData[0].displayName}
                  </span>
                  <strong>Embaixador</strong>
                </div>
              </div>
            </Frame>

            <ButtonContainer>
              <Button onClick={() => this.handleDownload()}>Download</Button>

              <Button onClick={() => firebase.auth().signOut()}>
                Sign out!
              </Button>
            </ButtonContainer>
          </span>
        ) : (
          <AuthScreen>
            <div>
              <h1>ProfileMaster</h1>

              <p>Faça a autenticação para obter sua moldura!</p>
            </div>

            <StyledFirebaseAuth
              uiConfig={this.uiConfig}
              firebaseAuth={firebase.auth()}
            />
          </AuthScreen>
        )}
      </Container>
    )
  }
}

export default SignIn
import { createGlobalStyle } from 'styled-components'

export default createGlobalStyle`
    * {
        margin: 0;
        padding: 0;
        outline: 0;
        box-sizing: border-box;
    }

    body {
        -webkit-font-smoothing: antialiased;
    }

    body, input, button {
        font: 16px Roboto, sans-serif;
    }

    #root {
        max-width: 960px;
        margin: 0 auto;
        padding: 40px 20px;
    }

    button {
        cursor: pointer;
    }
`
import React from 'react'
import ProfileMaster from './pages/ProfileMaster'
import GlobalStyle from './styles/global'

export const ExampleComponent = () => {
  return (
    <div>
      <ProfileMaster />
      <GlobalStyle />
    </div>
  )
}
