import React from 'react'

import { Container } from './styles'

const Button = ({ children, ...rest }) => (
  <Container type='button' {...rest}>
    {children}
  </Container>
)

export default Button
import React, { Component } from 'react'

import firebase from 'firebase'
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth'

import { Frame, ButtonContainer, Container, AuthScreen } from './styles'
import logo from '../../assets/gdgjf.png'

import html2canvas from 'html2canvas'

import Button from '../../components/Button'

firebase.initializeApp({
  apiKey: 'AIzaSyDflrkP2uTFEkOtT_KDRcDPgjjDvFd9MQE',
  authDomain: 'profilemaster-b369d.firebaseapp.com'
})

class SignIn extends Component {
  state = { isSignedIn: false, downloadURL: '' }

  uiConfig = {
    signInFlow: 'popup',
    signInOptions: [firebase.auth.GithubAuthProvider.PROVIDER_ID],
    callbacks: {
      signInSuccess: () => false
    }
  }

  componentDidMount = () => {
    firebase.auth().onAuthStateChanged((user) => {
      this.setState({ isSignedIn: !!user })
    })
  }

  download(dataUrl) {
    const element = document.createElement('a')
    element.setAttribute('href', dataUrl)
    element.setAttribute('download', 'profile-master.png')

    element.style.display = 'none'
    document.body.appendChild(element)

    element.click()

    document.body.removeChild(element)
  }

  handleDownload() {
    html2canvas(document.getElementById('capture'), {
      useCORS: true,
      height: 400,
      width: 400,
      scrollY: -window.scrollY
    }).then((canvas) => {
      const imgData = canvas.toDataURL('image/png')
      this.download(imgData)
    })
  }

  render() {
    return (
      <Container className='App'>
        {this.state.isSignedIn ? (
          <span>
            <Frame id='capture'>
              <img src={firebase.auth().currentUser.photoURL} alt='profile' />
              <div>
                <img src={logo} alt='Logo GDG Juiz de Fora' />
                <div>
                  <span>
                    {firebase.auth().currentUser.providerData[0].displayName}
                  </span>
                  <strong>Embaixador</strong>
                </div>
              </div>
            </Frame>

            <ButtonContainer>
              <Button onClick={() => this.handleDownload()}>Download</Button>

              <Button onClick={() => firebase.auth().signOut()}>
                Sign out!
              </Button>
            </ButtonContainer>
          </span>
        ) : (
          <AuthScreen>
            <div>
              <h1>ProfileMaster</h1>

              <p>Faça a autenticação para obter sua moldura!</p>
            </div>

            <StyledFirebaseAuth
              uiConfig={this.uiConfig}
              firebaseAuth={firebase.auth()}
            />
          </AuthScreen>
        )}
      </Container>
    )
  }
}

export default SignIn
import { createGlobalStyle } from 'styled-components'

export default createGlobalStyle`
    * {
        margin: 0;
        padding: 0;
        outline: 0;
        box-sizing: border-box;
    }

    body {
        -webkit-font-smoothing: antialiased;
    }

    body, input, button {
        font: 16px Roboto, sans-serif;
    }

    #root {
        max-width: 960px;
        margin: 0 auto;
        padding: 40px 20px;
    }

    button {
        cursor: pointer;
    }
`
import React from 'react'
import ProfileMaster from './pages/ProfileMaster'
import GlobalStyle from './styles/global'

export const ExampleComponent = () => {
  return (
    <div>
      <ProfileMaster />
      <GlobalStyle />
    </div>
  )
}
